const invariant = require('../utils/invariant');

/**
 * 元素基类，像积木一样一层一层的搭建
 */
class Element {
  constructor(zIndex = 0) {
    this.zIndex = zIndex; // TODO 用于控制渲染层级，后期可以用于重构渲染性能
    this.box = { x1: 0, y1: 0, x2: 0, y2: 0 };
  }

  setZIndex = (zIndex = 0) => {
    this.zIndex = zIndex;
  };

  clone = () => new Element();

  toString = () => 'Element()';


  /**
   * 绘制图形，产生图层，图层将用于最终的渲染
   * @param options { fill: '*', line: '-' }
   */
  // eslint-disable-next-line
  draw = (options) => {
    invariant(false, 'Element\'s method draw should be implemented by children Class.');
  };
}

module.exports = Element;
