/**
 *  what( var ) -> String
 *  - var: the var which want to typeof
 *  get what is the type of the input var.
 **/
const what = (v) => {
  if (v === null) {
    return 'null';
  }
  if (v !== Object(v)) {
    return typeof v;
  }
  return ({}).toString.call(v).slice(8, -1).toLowerCase();
};

/**
 * How to usage
 *

 what({}); // 'object'
 what({abc: 123}); // 'object'

 what([]); // 'array'
 what([123, 'abc']); // 'array'

 what(function() {}); // 'function'
 what(setTimeout); // 'function'

 what(/^what\.js$/); // 'regexp'

 what(new Date()); // 'date'

 what(null); // 'null'
 what(undefined); // 'undefined'

 what('abc'); // 'string'
 what(123); // 'number'
 what(12.3); // 'number'

 what(true); // 'boolean'
 what(false); // 'boolean'

 *
 */

const isNumber = v => what(v) === 'number';

const isString = v => what(v) === 'string';

const isArray = v => what(v) === 'array';

const isObject = v => what(v) === 'object';

const isBool = v => what(v) === 'boolean';

const isEmpty = v => v === null || v === undefined;

const isPoint = v => what(v) === 'Point';

const isBox = v => what(v) === 'Box';

module.exports = {
  isNumber,
  isString,
  isArray,
  isObject,
  isBool,
  isEmpty,
  isPoint,
  isBox,

  typeof: what,
};
